        public byte[] ForASCIIMethodIntoBytes(string pattern)
        {
            StringBuilder str = new();
            for (int i = 0; i < pattern.Length; i++)
            {
                if (pattern[i] > 47 && pattern[i] < 58 || pattern[i] > 64 && pattern[i] < 91 || pattern[i] > 96 && pattern[i] < 123)
                {
                    str.Append(pattern[i]);
                }
            }

            return Encoding.UTF8.GetBytes(str.ToString());
        }
