        public string BasicString(string pattern = "")
        {
            Regex splitRegex = new("\\W");
            var separatedString = splitRegex.Split(pattern);
            var joinedString = string.Join(string.Empty, separatedString);
            List<byte> byteString = new();
            foreach (char c in joinedString)
            {
                byteString.Add((byte)c);
            }

            return Convert.ToBase64String(byteString.ToArray());
        }

